mapper.update:
update picture
set status = 0
where id = #{id}

mapper.select:
select * from picture

    status = 1

        and divide_name = #{divideName}


order by  create_time desc limit ${pageNo}, ${pageSize};

select count(*) from picture

    status = 1

        and divide_name = #{divideName}


order by  create_time desc limit ${pageNo}, ${pageSize};

select * from picture

    status = 1

        and divide_name = #{divideName}


order by  create_time desc limit 0, 3;
