mapper.update:
update user_collection
set status = 0
where id = #{id} and user_id = #{userId};

mapper.select:
select id,news_id,user_id,create_time
from user_collection
where status = 1 and user_id = #{userId}
order by create_time desc  limit ${pageNo}, ${pageSize};

select count(*)
from user_collection
where status = 1 and user_id = #{userId}
